import { PropsWithChildren, ReactElement } from 'react'
import { FaApple, FaFacebook } from 'react-icons/fa'
import { FcGoogle } from 'react-icons/fc'
import { cn } from 'lib/utils'
import { Button } from './button'

interface SocialButtonProps extends PropsWithChildren {
  type: 'facebook' | 'google' | 'apple'
  variant: 'solid' | 'outline' | 'transparent' | 'icon'
}

const brandToIconMap: Record<SocialButtonProps['type'], ReactElement> = {
  google: <FcGoogle className="h-[24px] w-[24px]" />,
  facebook: <FaFacebook className="h-[24px] w-[24px]" />,
  apple: <FaApple className="h-[24px] w-[24px]" />,
}

const brandToClassName: Record<
  SocialButtonProps['type'],
  Partial<Record<SocialButtonProps['variant'], string>>
> = {
  google: {
    solid: 'bg-[#4285F4] hover:bg-[#4285F4] text-white',
    outline: 'bg-white hover:bg-white border-[1px] border-black text-black',
    transparent: 'bg-white hover:bg-white shadow-md text-black',
    icon: 'bg-white hover:bg-white shadow-md p-2',
  },
  facebook: {
    solid: 'bg-[#4285F4] hover:bg-[#4285F4] text-white',
    outline: 'bg-white hover:bg-white border-[1px] border-[#1877F2] text-[#1877F2]',
    transparent: 'bg-white hover:bg-white shadow-md text-[#1877F2]',
    icon: 'bg-white hover:bg-white shadow-md p-2 text-[#1877F2]',
  },
  apple: {
    solid: 'bg-black hover:bg-black text-white',
    outline: 'bg-white hover:bg-white border-[1px] border-black text-black',
    transparent: 'bg-white hover:bg-white shadow-md text-black',
    icon: 'bg-white hover:bg-white shadow-md p-2 text-black',
  },
}

const SocialButton = ({ type, variant, children }: SocialButtonProps) => (
  <Button className={cn(brandToClassName[type][variant])}>
    {brandToIconMap[type]}
    {variant !== 'icon' && children}
  </Button>
)

export { SocialButton, type SocialButtonProps }

import { Meta, StoryObj } from '@storybook/react'
import { SocialButton, SocialButtonProps } from './socialButton'

const SocialButtonStory = (props: SocialButtonProps) => <SocialButton {...props} />

type Story = StoryObj<typeof meta>

export const GoogleSolid: Story = {}
GoogleSolid.args = {
  type: 'google',
  children: 'Sign in with Google',
  variant: 'solid',
}

export const GoogleOutline: Story = {}
GoogleOutline.args = {
  ...GoogleSolid.args,
  variant: 'outline',
}

export const GoogleTransparent: Story = {}
GoogleTransparent.args = {
  ...GoogleSolid.args,
  variant: 'transparent',
}

export const GoogleIcon: Story = {}
GoogleIcon.args = {
  ...GoogleSolid.args,
  variant: 'icon',
}

export const FacebookSolid: Story = {}
FacebookSolid.args = {
  type: 'facebook',
  children: 'Sign in with Facebook',
  variant: 'solid',
}

export const FacebookOutline: Story = {}
FacebookOutline.args = {
  ...FacebookSolid.args,
  variant: 'outline',
}

export const FacebookTransparent: Story = {}
FacebookTransparent.args = {
  ...FacebookSolid.args,
  variant: 'transparent',
}

export const FacebookIcon: Story = {}
FacebookIcon.args = {
  ...FacebookSolid.args,
  variant: 'icon',
}

export const AppleSolid: Story = {}
AppleSolid.args = {
  type: 'apple',
  variant: 'solid',
  children: 'Sign in with Apple',
}

export const AppleOutline: Story = {}
AppleOutline.args = {
  ...AppleSolid.args,
  variant: 'outline',
}

export const AppleTransparent: Story = {}
AppleTransparent.args = {
  ...AppleSolid.args,
  variant: 'transparent',
}

export const AppleIcon: Story = {}
AppleIcon.args = {
  ...AppleSolid.args,
  variant: 'icon',
}

const meta: Meta<typeof SocialButtonStory> = {
  title: 'Components/SocialButton/SocialButton',
  component: SocialButtonStory,
}

export default meta
